import { useState,useEffect } from "react";
import axios from "axios"

function App() {

  const [coin,setCoin] = useState("BTC")
  const [coinData,setCoinData] =  useState({})

  useEffect(()=>{
    const fetchData = async ()=>{
      try{
        const payload = {
          "code":coin
        }
        console.log(payload)
        const response = await axios.post('http://localhost:3000/api/v1/singleCoinPrice',payload)
        console.log(response.data)
        setCoinData(response.data)
      }catch(err){
        console.log("Error in fetching coin data...")
      }
    }
    fetchData()
  },[])

  return (
    <div>
      <h1 align="center">Live Coin Data Dashboard</h1>
      <table>
        <thead>
          <tr>
            <th>Index</th>
            <th>COIN NAME</th>
            <th>Price</th>
            <th>Market Cap</th>
            <th>Volume</th>
          </tr>
        </thead>
        <tbody>
          {coinData.map((item,index)=>(
            <tr>{index+1}</tr>,
            <tr>{item.COIN_NAME}</tr>,
            <tr>{item.Price}</tr>,
            <tr>{item.MarketCap}</tr>,
            <tr>{item.Volume}</tr>
          ))}
        </tbody>
      </table>
    </div>

  );
}

export default App;
